import React,{useState} from 'react';
import { Container ,Row,Col} from 'react-bootstrap';
import { useSelector ,useDispatch} from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { addToCart } from '../actions/cartAction';

const CartScreen=()=>{

    const cartState=useSelector(state=>state.cartReducer)
    const cartItems=cartState.cartItems;
    const dispatch=useDispatch();


    return (
            <>

            <Container>
                <Row>
                    <Col md={6}>
                        <h1>My Cart</h1>
                        <Row>
                        {
                            cartItems.map(item=>(
                                <>
                                <Col md={7}>
                                <h5>{item.name} [{item.varient}]</h5>
                                 <h6>
                                {" "}
                                Price : {item.quantity} X {item.prices[0][item.varient]} ={item.price}

                                 </h6>

                                <h6>
                                    Quantity:
                                    <button className="fa fa-minus-circle text-danger" style={{cursor:"pointer"}} OnClick={()=>{
                                        dispatch(addToCart(item,item.quantity-1,item.varient));
                                        console.log("clicked");
                                        
                                    }}></button>
                                    {" "}
                                     &nbsp;
                                     {item.quantity} &nbsp;

                                     <button className="fa fa-plus-circle text-success" style={{cursor:"pointer"}} OnClick={()=>{
                                        dispatch(addToCart(item,item.quantity+1,item.varient))
                                    }}></button> &nbsp;
                                </h6>

                                <hr></hr>
                                </Col>
                                <Col md={5}>
                                
                                <img alt={item.name} src={item.image} style={{width:'80px',height:'80px'}}/>
                                </Col>
                                
                                
                                </>


                            ))



                        }

                        </Row>
                    </Col>
                    <Col md={4}>
                        <h1>Payment Info</h1>
                    </Col>




                </Row>





            </Container>
            


            
            
            
            </>

    )


}

export default CartScreen;
